mapper.select:
select bookId, bookName, b.authorName, c.cateName, bookStock, a.regDate from vam_book a left outer join vam_author b on a.authorId = b.authorId left outer join vam_bcate c on a.cateCode = c.cateCode
where (bookName like concat ('%', #{keyword}, '%'))
order by bookId desc limit #{skip}, #{amount}


select bookId, bookName, (select authorName from vam_author where authorId =vam_book.authorId) authorName, 
	authorId, publeYear, publisher, cateCode, bookPrice, bookStock, bookDiscount, bookIntro, bookContents, regDate, updateDate 
from vam_book where bookId = #{bookId}

mapper.update:

Update vam_book set 
	bookName = #{bookName}
	, authorId = #{authorId}
	, publeYear = #{publeYear}
	, publisher = #{publisher}
	, cateCode = ${cateCode}
	, bookPrice = #{bookPrice}
	, bookStock = #{bookStock}
	, bookDiscount = #{bookDiscount}
	, bookIntro = #{bookIntro}
	, bookContents = #{bookContents}
	, updateDate = sysdate()
Where bookId = ${bookId}
